import React, { useState, useRef } from 'react';
import { Mic, Square, MessageSquare, RefreshCw } from 'lucide-react';

const AudioChat = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [messages, setMessages] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState('');
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const bottomRef = useRef(null);

  const scrollToBottom = () => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const startRecording = async () => {
    try {
      setError('');
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        await processAudio(audioBlob);
      };

      mediaRecorder.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Error accessing microphone:', error);
      setError('Error accessing microphone. Please ensure microphone permissions are granted.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
    }
  };

  const processAudio = async (audioBlob) => {
    setIsProcessing(true);
    setError('');
    try {
      const reader = new FileReader();
      reader.readAsDataURL(audioBlob);

      reader.onloadend = async () => {
        try {
          const base64Audio = reader.result.split(',')[1];
          setMessages(prev => [...prev, { type: 'user', content: 'Audio message sent...', loading: true }]);
          scrollToBottom();

          const response = await fetch('http://localhost:8000/process-audio', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              audio: base64Audio
            })
          });

          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }

          const data = await response.json();

          // Update messages with transcription and response
          setMessages(prev => [
            ...prev.filter(msg => !msg.loading),
            { type: 'user', content: data.transcription },
            { type: 'assistant', content: data.ai_response }
          ]);
          scrollToBottom();

          // Play audio response
          if (data.audio_response) {
            const audio = new Audio(`data:audio/mp3;base64,${data.audio_response}`);
            audio.play().catch(e => console.error('Error playing audio:', e));
          }
        } catch (error) {
          console.error('Error processing request:', error);
          setError(`Error processing audio: ${error.message}`);
          setMessages(prev => prev.filter(msg => !msg.loading));
        }
      };
    } catch (error) {
      console.error('Error processing audio:', error);
      setError(`Error processing audio: ${error.message}`);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-800">
      {/* Chat container */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message, index) => (
          <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
            <div className={`max-w-[80%] rounded-lg p-3 ${
              message.type === 'user' 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-700 text-white'
            } ${message.loading ? 'opacity-50' : ''}`}>
              {message.content}
            </div>
          </div>
        ))}
        {error && (
          <div className="p-3 text-red-400 bg-red-900 bg-opacity-50 rounded">
            {error}
          </div>
        )}
        <div ref={bottomRef} />
      </div>

      {/* Control bar */}
      <div className="border-t border-gray-700 p-4">
        <div className="max-w-4xl mx-auto flex items-center gap-4">
          <button
            onClick={isRecording ? stopRecording : startRecording}
            disabled={isProcessing}
            className={`p-3 rounded-full ${
              isRecording 
                ? 'bg-red-500 hover:bg-red-600' 
                : 'bg-blue-500 hover:bg-blue-600'
            } text-white transition-colors disabled:opacity-50`}
          >
            {isRecording ? <Square size={20} /> : <Mic size={20} />}
          </button>

          <div className="flex-1">
            <div className="h-10 rounded-lg bg-gray-700 flex items-center px-4 text-gray-400">
              {isProcessing ? 'Processing audio...' : 'Click the microphone to start recording'}
            </div>
          </div>

          {isProcessing && (
            <RefreshCw className="animate-spin text-gray-400" size={20} />
          )}
        </div>
      </div>
    </div>
  );
};

export default AudioChat;
